public static boolean isFeatureEnabled(DAXCustomIFeature _feature)
{
    if (_feature == null)
    {
        throw error(Error::wrongUseOfFunction(funcName()));
    }
    DAXCustomFeatureManagementState featureManagementState = DAXCustomFeatureManagementState::findByFeatureName(DAXCustomFeatureStateProvider::featureName(_feature));

    if(featureManagementState == null)
    {
        return false;
    }

    boolean ret = featureManagementState.IsEnabled;

    if(featureManagementState.isComapnyBase())
    {
        ret = ret && DAXCustomFeatureManagementStateCompany::findOrCreate(featureManagementState.RecId).IsEnabled;
    }

    return ret;
}

public static str featureName(DAXCustomIFeature _feature)
{
    if (_feature == null)
    {
        throw error(Error::wrongUseOfFunction(funcName()));
    }

    return classId2Name(classIdGet(_feature));
}
