static public DAXCustomFeatureManagementStateCompany find(RecId _featureStateRecId,boolean _selectForUpdate = false)
{
    DAXCustomFeatureManagementStateCompany  FeatureManagementStateCompany;

    if(_selectForUpdate)
        FeatureManagementStateCompany.selectForUpdate(true);

    select FeatureManagementStateCompany where
        FeatureManagementStateCompany.FeatureState == _featureStateRecId;


    return FeatureManagementStateCompany;
}

static public DAXCustomFeatureManagementStateCompany findOrCreate(RecId _featureStateRecId,boolean _selectForUpdate = false)
{
    DAXCustomFeatureManagementStateCompany  FeatureManagementStateCompany = DAXCustomFeatureManagementStateCompany::find(_featureStateRecId,_selectForUpdate);

    if(!FeatureManagementStateCompany)
    {
        FeatureManagementStateCompany.FeatureState = _featureStateRecId;
        FeatureManagementStateCompany.insert();
    }

    return FeatureManagementStateCompany;
}

public static void enableFeature(RecId _featureStateRecId,CompanyId _companyId = curExt())
{

    boolean isFeatureEnabled = false;

    ttsbegin;
    changecompany(_companyId)
    {

        DAXCustomFeatureManagementStateCompany featureState = DAXCustomFeatureManagementStateCompany::findOrCreate(_featureStateRecId, true);

        if (featureState)
        {
            featureState.IsEnabled = true;

            if (featureState.validateWrite())
            {
                featureState.write();
                isFeatureEnabled = true;
            }

        }
    }

    ttscommit;


}

public static void disableFeature(RecId _featureStateRecId,CompanyId _companyId = curExt())
{
    boolean isFeatureDisabled = false;

    ttsbegin;
    changecompany(_companyId)
    {

        DAXCustomFeatureManagementStateCompany featureState = DAXCustomFeatureManagementStateCompany::find(_featureStateRecId, true);

        if (featureState)
        {

            featureState.IsEnabled = false;

            if (featureState.validateWrite())
            {
                featureState.write();
                isFeatureDisabled = true;
            }
        }
    }


    ttscommit;

}
